# Remove at from hash with a map function to replace @ symbol with empty space
 at
$data_without_at = $data.map{
  | string | string.gsub("@", "")
}

#---------------------------------------------------------------------------------------#
#---------------------------------------------------------------------------------------#

# Combien y a-t-il de handle dans cette array ?
def sum
  puts "On a #{$data.size} handles dans cette array."
end

#---------------------------------------------------------------------------------------#

# Quel est le handle le plus court de cette liste ?

def shortest
# Use min_by function to retrieve element with the shortest length
  puts "Le handle le plus court est #{$data.min_by(&:length)}."
end

#---------------------------------------------------------------------------------------#

# Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)

def size
# Select hash with length == 5
  result = $data_without_at.select {
    | key | key.length == 5
  }.count
puts "Il y a #{result} handles qui contiennent 5 caractères."
end

#---------------------------------------------------------------------------------------#

# Combien commencent par une majuscule (première lettre juste après le @) ?

def find_uppercase
# Use grep function to isolate elements with uppercase then count them
  uppercase_items = $data_without_at.grep(/^[A-Z]/).count
  puts "Il y a #{uppercase_items} handles qui commencent par une majuscule."
end

#---------------------------------------------------------------------------------------#

# Trie la liste des handles par ordre alphabétique.

# Use sort to display elements alphabetically
def sort
  puts "Voici la liste des handles triés par ordre alphabétique: #{$data.sort}."
end

#---------------------------------------------------------------------------------------#

# Trie la liste des handles par ordre croissant

# Sort elements in the array by length comparing elements one by one
def sort_asc
  puts "Tri des handles par ordre croissant: "
  result = $data.sort {
    | left, right | left.length <=> right.length}
    puts result
end

#---------------------------------------------------------------------------------------#

# Quelle est la position dans l'array de la personne @epenser ?

# Retrieve the index of the element 
def find_index
  puts "la position dans l'array de la personne @epenser est #{$data.index("@epenser")}."
end

#---------------------------------------------------------------------------------------#

# Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)

def group_by_size
  puts "Répartition des handles par taille: " 
  # Group elements by length then return a new hash with the results of counting each element
  puts $data_without_at.group_by(&:length).transform_values(&:count)
end

