# Initialize the menu options
$menu_options = [
  {
    name: "Combien y a-t-il de handle dans cette array ?",
    method: method(:sum)
  },
  {
    name: "Quel est le handle le plus court de cette liste ?",
    method: method(:shortest)
  },
  {
    name: "Combien y-a-t'il de handle contenant 5 caractères ?",
    method: method(:size)
  },
  {
    name: "Combien commencent par une majuscule (première lettre juste après le @) ?",
    method: method(:find_uppercase)
  },
  {
    name: "Trie la liste des handles par ordre alphabétique.",
    method: method(:sort)
  },
  {
    name: "Trie la liste des handles par ordre croissant",
    method: method(:sort_asc)
  },
  {
    name: "Quelle est la position dans l'array de la personne @epenser ?",
    method: method(:find_index)
  },
  {
    name: "Sors-moi une répartition des handle par taille.",
    method: method(:group_by_size)
  }
]

# Define the method to display the menu
def display_menu
  system("clear")
  puts "Sélectionnez une des options ci-dessous en tapant le numéro correspondant:\n"
  $menu_options.each_with_index do |option, index|
    puts "#{index + 1}. #{option[:name]}"
  end
  print "\nEnter your choice: "
  choice = gets.chomp.to_i
  if choice > 0 && choice <= $menu_options.length
    $menu_options[choice - 1][:method].call
    puts "\nPress enter to continue..."
    gets
    display_menu
  else
    puts "Invalid choice. Please try again."
    gets
    display_menu
  end
end

# Call the method to display the menu
display_menu


